def calculate_nico_joint(angle, angle_range, nico_joint_range):
    x1, x2 = angle_range
    y1, y2 = nico_joint_range
    if angle < x1:
        return y1
    if angle > x2:
        return y2
    k = (y2 - y1) / (x2 - x1)
    q = y1 - (k * x1)
    y = k * angle + q
    return round(y)


