import matplotlib.pyplot as plt
from mpl_toolkits.mplot3d import Axes3D

def plotuj(pole2d):

    x = [point[0] for point in pole2d]
    y = [point[1] for point in pole2d]
    z = [point[2] for point in pole2d]

    fig = plt.figure()
    ax = fig.add_subplot(111, projection='3d')
    ax.scatter(x, y, z, c='r', marker='o')  # c je farba, marker určuje tvar bodu
    plt.show()

import numpy as np

body_3d = [(1,1,4),(2,2,5),(3,1,6)]


# Konvertovanie bodov na numpy pole
points = np.array(body_3d)

# Definovanie bočných pohľadov
view_angles = [(90, 0), (180, 0)]  # (azimutálny uhol, elevačný uhol)

# Vytvorenie 2D grafiky
fig, axs = plt.subplots(1, len(view_angles), figsize=(10, 5))

# Iterácia cez bočné pohľady
for i, (azim, elev) in enumerate(view_angles):
    # Projekcia bodov na rovinu
    projected_points = points[:, [1, 2]]  # Získanie y a z súradníc, čo odpovedá bočnému pohľadu
    
    # Vykreslenie projekcií bodov na 2D graf
    axs[i].scatter(projected_points[:, 0], projected_points[:, 1], c='r', marker='o')  # c je farba, marker určuje tvar bodu
    axs[i].set_title(f'Side View Angle: {azim}°')

# Zobrazenie grafu
plt.tight_layout()
plt.show()
